type CacheEntry<V> = {
  value: V
}
export class Cache<K, V> {
  private _map = new Map<K, CacheEntry<V>>()

  get(key: K): V | undefined {
    return this._map.get(key)?.value
  }

  set(key: K, value: V): void {
    this._map.set(key, { value })
  }

  getOrCreate(key: K, create: () => V): V {
    const cached = this._map.get(key)
    if (cached) {
      return cached.value
    }
    const value = create()
    this.set(key, value)
    return value
  }
}
/**
 * Converts the first character of a word to upper case.
 */
export function capitalize<T extends string>(self: T): Capitalize<T> {
  if (self.length === 0) return self as Capitalize<T>

  return (self[0].toUpperCase() + self.slice(1)) as Capitalize<T>
}

/**
 * Converts the first character of a word to lower case.
 */
export function uncapitalize<T extends string>(self: T): Uncapitalize<T> {
  return (self.substring(0, 1).toLowerCase() + self.substring(1)) as Uncapitalize<T>
}
export interface Dictionary<T> {
  [key: string]: T
}

export function keyBy<P extends PropertyKey, T extends { [key in P]: string }>(
  collection: readonly T[],
  prop: P,
): Dictionary<T> {
  const acc: Dictionary<T> = {}

  for (const obj of collection) {
    const key = obj[prop]
    acc[key] = obj
  }

  return acc
}
export type LazyProperty<T> = {
  get: () => T
}

export function lazyProperty<T>(compute: () => T): LazyProperty<T> {
  let resultContainer: undefined | { value: T }

  return {
    get() {
      if (resultContainer) {
        return resultContainer.value
      }
      resultContainer = { value: compute() }
      return resultContainer.value
    },
  }
}
