import { Cache } from '@prisma/client-common'
import { mapObjectValues } from '@prisma/internals'

import { dmmfToJSModelName } from '../model/utils/dmmfToJSModelName'
import { ExtensionArgs, ResultArg, ResultArgsFieldCompute } from '../types/exported/ExtensionArgs'
import { Omission, Selection } from '../types/exported/JsApi'

export type ComputedField = {
  name: string
  needs: string[]
  compute: ResultArgsFieldCompute
}

export type ComputedFieldsMap = {
  [fieldName: string]: ComputedField
}

/**
 * Given the list of previously resolved computed fields, new extension and dmmf model name, produces a map
 * of all computed fields that may be applied to this model, accounting for all previous and past extensions.
 *
 * All naming conflicts which could be produced by the plain list of extensions are resolved as follows:
 * - extension, that declared later always wins
 * - in a single extension, specific model takes precedence over $allModels
 *
 * Additionally, resolves all `needs` dependencies down to the model fields. For example,
 * if `nameAndTitle` field depends on `fullName` computed field and `title` model field and
 * `fullName` field depends on `firstName` and `lastName` field, full list of `nameAndTitle` dependencies
 * would be `firstName`, `lastName`, `title`.
 * @param previousComputedFields
 * @param extension
 * @param dmmfModelName
 * @returns
 */
export function getComputedFields(
  previousComputedFields: ComputedFieldsMap | undefined,
  extension: ExtensionArgs,
  dmmfModelName: string,
) {
  const jsName = dmmfToJSModelName(dmmfModelName)
  if (!extension.result || !(extension.result.$allModels || extension.result[jsName])) {
    return previousComputedFields
  }

  return resolveDependencies({
    ...previousComputedFields,
    ...getComputedFieldsFromModel(extension.name, previousComputedFields, extension.result.$allModels),
    ...getComputedFieldsFromModel(extension.name, previousComputedFields, extension.result[jsName]),
  })
}

export function resolveDependencies(computedFields: ComputedFieldsMap): ComputedFieldsMap {
  const cache = new Cache<string, string[]>()
  const resolveNeeds = (fieldName: string, visitedFields: Set<string>) => {
    return cache.getOrCreate(fieldName, () => {
      if (visitedFields.has(fieldName)) {
        return [fieldName]
      }
      visitedFields.add(fieldName)
      if (!computedFields[fieldName]) {
        return [fieldName]
      }
      return computedFields[fieldName].needs.flatMap((fieldDep) => resolveNeeds(fieldDep, visitedFields))
    })
  }

  return mapObjectValues(computedFields, (field) => {
    return {
      ...field,
      needs: resolveNeeds(field.name, new Set()),
    }
  })
}

function getComputedFieldsFromModel(
  name: string | undefined,
  previousComputedFields: ComputedFieldsMap | undefined,
  modelResult: ResultArg | undefined,
): ComputedFieldsMap {
  if (!modelResult) {
    return {}
  }

  return mapObjectValues(modelResult, ({ needs, compute }, fieldName) => ({
    name: fieldName,
    needs: needs ? Object.keys(needs).filter((key) => needs[key]) : [],
    compute: composeCompute(previousComputedFields, fieldName, compute),
  }))
}

function composeCompute(
  previousComputedFields: ComputedFieldsMap | undefined,
  fieldName: string,
  nextCompute: ResultArgsFieldCompute,
): ResultArgsFieldCompute {
  const previousCompute = previousComputedFields?.[fieldName]?.compute
  if (!previousCompute) {
    return nextCompute
  }
  return (model) => {
    return nextCompute({ ...model, [fieldName]: previousCompute(model) })
  }
}

export function computeEngineSideSelection(
  selection: Selection,
  computedFields: ComputedFieldsMap | undefined,
): Selection {
  if (!computedFields) {
    return selection
  }
  const result = { ...selection }

  for (const field of Object.values(computedFields)) {
    if (!selection[field.name]) {
      continue
    }

    for (const dependency of field.needs) {
      result[dependency] = true
    }
  }
  return result
}

/**
 * Given user-supplied omissions, computes the results to send to the engine, taking
 * into account dependencies of the computed field. Consider following example:
 *
 * ```
 * const xprisma = prisma.$extends({
 *       result: {
 *         user: {
 *           sanitizedPassword: {
 *             needs: { password: true },
 *             compute(user) {
 *               return sanitze(user.password)
 *             },
 *           },
 *         },
 *       },
 * })
 *
 * const user = await xprisma.user.findFirstOrThrow({
 *       omit: {
 *         password: true,
 *       },
 * })
 * ```
 *
 * In that case, user wants to omit the `password` but not `sanitizedPassword`.
 * Since `sanitizedPassword` can not be computed without `password`, we can not let
 * the engine handle omission in this case - we have to still query `password` from the
 * database and omit it on the client, after computing `sanitizedPassword`.
 *
 * This function removes the omission (thus, including the field into result set) if it is a dependency of
 * a non-omitted computed field. Client-side omission after we get a response is handled by `applyResultExtensions`
 *
 * @param omission
 * @param computedFields
 * @returns
 */
export function computeEngineSideOmissions(
  omission: Omission,
  computedFields: ComputedFieldsMap | undefined,
): Omission {
  if (!computedFields) {
    return omission
  }
  const result = { ...omission }

  for (const field of Object.values(computedFields)) {
    if (omission[field.name]) {
      continue
    }

    for (const dependency of field.needs) {
      delete result[dependency]
    }
  }
  return result
}
import { Cache, lazyProperty } from '@prisma/client-common'

import { dmmfToJSModelName } from '../model/utils/dmmfToJSModelName'
import { ClientArg, ExtensionArgs, ModelArg, QueryOptionsCb } from '../types/exported/ExtensionArgs'
import { BatchQueryOptionsCb, QueryOptionsPrivate } from '../types/internal/ExtensionsInternalArgs'
import { ComputedFieldsMap, getComputedFields } from './resultUtils'

class MergedExtensionsListNode {
  private computedFieldsCache = new Cache<string, ComputedFieldsMap | undefined>()
  private modelExtensionsCache = new Cache<string, ModelArg | undefined>()
  private queryCallbacksCache = new Cache<string, QueryOptionsCb[]>()

  private clientExtensions = lazyProperty(() => {
    if (!this.extension.client) {
      return this.previous?.getAllClientExtensions()
    }

    return {
      ...this.previous?.getAllClientExtensions(),
      ...this.extension.client,
    }
  })

  private batchCallbacks = lazyProperty(() => {
    const previous: BatchQueryOptionsCb[] = this.previous?.getAllBatchQueryCallbacks() ?? []
    const newCb = (this.extension as QueryOptionsPrivate).query?.$__internalBatch
    if (!newCb) {
      return previous
    }
    return previous.concat(newCb)
  })

  constructor(
    public extension: ExtensionArgs,
    public previous?: MergedExtensionsListNode,
  ) {}

  getAllComputedFields(dmmfModelName: string): ComputedFieldsMap | undefined {
    return this.computedFieldsCache.getOrCreate(dmmfModelName, () => {
      return getComputedFields(this.previous?.getAllComputedFields(dmmfModelName), this.extension, dmmfModelName)
    })
  }

  getAllClientExtensions(): ClientArg | undefined {
    return this.clientExtensions.get()
  }

  getAllModelExtensions(dmmfModelName: string): ModelArg | undefined {
    return this.modelExtensionsCache.getOrCreate(dmmfModelName, () => {
      const jsModelName = dmmfToJSModelName(dmmfModelName)
      if (!this.extension.model || !(this.extension.model[jsModelName] || this.extension.model.$allModels)) {
        return this.previous?.getAllModelExtensions(dmmfModelName)
      }

      return {
        ...this.previous?.getAllModelExtensions(dmmfModelName),
        ...this.extension.model.$allModels,
        ...this.extension.model[jsModelName],
      }
    })
  }

  // eslint-disable-next-line @typescript-eslint/no-redundant-type-constituents
  getAllQueryCallbacks(jsModelName: string | '$none', operation: string) {
    return this.queryCallbacksCache.getOrCreate(`${jsModelName}:${operation}`, () => {
      const prevCbs = this.previous?.getAllQueryCallbacks(jsModelName, operation) ?? []
      const newCbs: QueryOptionsCb[] = []
      const query = this.extension.query

      if (!query || !(query[jsModelName] || query['$allModels'] || query[operation] || query['$allOperations'])) {
        return prevCbs
      }

      if (query[jsModelName] !== undefined) {
        if (query[jsModelName][operation] !== undefined) {
          newCbs.push(query[jsModelName][operation])
        }

        // when the model-bound extension has a wildcard for the operation
        if (query[jsModelName]['$allOperations'] !== undefined) {
          newCbs.push(query[jsModelName]['$allOperations'])
        }
      }

      // when the extension isn't model-bound, apply it to all models
      // '$none' is a special case for top-level operations without model
      if (jsModelName !== '$none' && query['$allModels'] !== undefined) {
        if (query['$allModels'][operation] !== undefined) {
          newCbs.push(query['$allModels'][operation])
        }

        // when the non-model-bound extension has a wildcard for the operation
        if (query['$allModels']['$allOperations'] !== undefined) {
          newCbs.push(query['$allModels']['$allOperations'])
        }
      }

      // when the extension is not bound to a model & is a top-level operation
      if (query[operation] !== undefined) {
        newCbs.push(query[operation] as QueryOptionsCb)
      }

      // when the extension is not bound to a model & is any top-level operation
      if (query['$allOperations'] !== undefined) {
        newCbs.push(query['$allOperations'] as QueryOptionsCb)
      }

      return prevCbs.concat(newCbs)
    })
  }

  getAllBatchQueryCallbacks() {
    return this.batchCallbacks.get()
  }
}

/**
 * Class that holds the list of all extensions, applied to particular instance,
 * as well as resolved versions of the components that need to apply on
 * different levels. Main idea of this class: avoid re-resolving as much of the
 * stuff as possible when new extensions are added while also delaying the
 * resolve until the point it is actually needed. For example, computed fields
 * of the model won't be resolved unless the model is actually queried. Neither
 * adding extensions with `client` component only cause other components to
 * recompute.
 */
export class MergedExtensionsList {
  private constructor(private head?: MergedExtensionsListNode) {}

  static empty() {
    return new MergedExtensionsList()
  }

  static single(extension: ExtensionArgs) {
    return new MergedExtensionsList(new MergedExtensionsListNode(extension))
  }

  isEmpty(): boolean {
    return this.head === undefined
  }

  append(extension: ExtensionArgs) {
    return new MergedExtensionsList(new MergedExtensionsListNode(extension, this.head))
  }

  getAllComputedFields(dmmfModelName: string): ComputedFieldsMap | undefined {
    return this.head?.getAllComputedFields(dmmfModelName)
  }

  getAllClientExtensions() {
    return this.head?.getAllClientExtensions()
  }

  getAllModelExtensions(dmmfModelName: string) {
    return this.head?.getAllModelExtensions(dmmfModelName)
  }

  getAllQueryCallbacks(jsModelName: string, operation: string) {
    return this.head?.getAllQueryCallbacks(jsModelName, operation) ?? []
  }

  getAllBatchQueryCallbacks() {
    return this.head?.getAllBatchQueryCallbacks() ?? []
  }
}
