function rootLayer(client: Client): CompositeProxyLayer {
  const prototype = Object.getPrototypeOf(client._originalClient)
  const allKeys = [...new Set(Object.getOwnPropertyNames(prototype))]

  return {
    getKeys() {
      return allKeys
    },

    getPropertyValue(prop) {
      return client[prop]
    },
  }
