  const newClient = Object.create(this._originalClient, {
    _extensions: { value: this._extensions.append(extension) },
    _appliedParent: { value: this, configurable: true },
    $on: { value: undefined },
  }) as Client

  return applyModelsAndClientExtensions(newClient)
}
