type GlobalThisWithPrismaInstrumentation = typeof globalThis & {
  [GLOBAL_INSTRUMENTATION_KEY]?: PrismaInstrumentationGlobalValue
} & {
  [K in typeof GLOBAL_VERSIONED_INSTRUMENTATION_KEY]?: PrismaInstrumentationGlobalValue
