export class PrismaClientConstructorValidationError extends Error {
  constructor(message: string) {
    super(message + `\nRead more at https://pris.ly/d/client-constructor`)
    this.name = 'PrismaClientConstructorValidationError'
  }
  get [Symbol.toStringTag]() {
    return 'PrismaClientConstructorValidationError'
  }
}
setClassName(PrismaClientConstructorValidationError, 'PrismaClientConstructorValidationError')
import { hasBatchIndex } from '@prisma/client-runtime-utils'

/**
 * Waits for result of batch $transaction and picks the best possible error to report if any
 * of the request fails. Best error is determined as follows:
 *
 * - if engine have reported an error without batch request index, then the batch is immediately rejected
 * with this error without waiting for other promises
 * - if engine have reported and index of failed request in the batch and that index matches the position of the
 * particular request in the batch, batch is rejected with that error
 * - if batch request index is reported and it does not match current request position, wait for other requests. If no indices
 * match request positions, reject with the earliest error in the batch
 *
 * @param promises
 * @returns
 */
export function waitForBatch<T extends PromiseLike<unknown>[]>(
  promises: T,
): Promise<{ [K in keyof T]: Awaited<T[K]> }> {
  if (promises.length === 0) {
    return Promise.resolve([] as { [K in keyof T]: Awaited<T[K]> })
  }
  return new Promise((resolve, reject) => {
    const successfulResults = new Array(promises.length) as { [K in keyof T]: Awaited<T[K]> }
    let bestError: unknown = null
    let done = false
    let settledPromisesCount = 0

    const settleOnePromise = () => {
      if (done) {
        return
      }
      settledPromisesCount++
      if (settledPromisesCount === promises.length) {
        done = true
        if (bestError) {
          reject(bestError)
        } else {
          resolve(successfulResults)
        }
      }
    }

    const immediatelyReject = (error: unknown) => {
      if (!done) {
        done = true
        reject(error)
      }
    }

    for (let i = 0; i < promises.length; i++) {
      promises[i].then(
        (result) => {
          successfulResults[i] = result
          settleOnePromise()
        },
        (error) => {
          if (!hasBatchIndex(error)) {
            immediatelyReject(error)
            return
          }

          if (error.batchRequestIdx === i) {
            immediatelyReject(error)
          } else {
            if (!bestError) {
              bestError = error
            }
            settleOnePromise()
          }
        },
      )
    }
  })
}
/**
 * List of properties that won't throw exception on access and return undefined instead
 */
const allowList = new Set([
  'toJSON', // used by JSON.stringify
  '$$typeof', // used by old React tooling
  'asymmetricMatch', // used by Jest
  Symbol.iterator, // used by various JS constructs/methods
  Symbol.toStringTag, // Used by .toString()
  Symbol.isConcatSpreadable, // Used by Array#concat,
  Symbol.toPrimitive, // Used when getting converted to primitive values
])
/**
 * Generates more strict variant of an enum which, unlike regular enum,
 * throws on non-existing property access. This can be useful in following situations:
 * - we have an API, that accepts both `undefined` and `SomeEnumType` as an input
 * - enum values are generated dynamically from DMMF.
 *
 * In that case, if using normal enums and no compile-time typechecking, using non-existing property
 * will result in `undefined` value being used, which will be accepted. Using strict enum
 * in this case will help to have a runtime exception, telling you that you are probably doing something wrong.
 *
 * Note: if you need to check for existence of a value in the enum you can still use either
 * `in` operator or `hasOwnProperty` function.
 *
 * @param definition
 * @returns
 */
export function makeStrictEnum<T extends Record<PropertyKey, string | number>>(definition: T): T {
  return new Proxy(definition, {
    get(target, property) {
      if (property in target) {
        return target[property]
      }
      if (allowList.has(property)) {
        return undefined
      }
      throw new TypeError(`Invalid enum value: ${String(property)}`)
    },
  })
}
// https://runtime-keys.proposal.wintercg.org/
export type RuntimeName = 'workerd' | 'deno' | 'netlify' | 'node' | 'bun' | 'edge-light' | '' /* unknown */

/**
 * Indicates if running in Node.js or a Node.js compatible runtime.
 *
 * **Note:** When running code in Bun and Deno with Node.js compatibility mode, `isNode` flag will be also `true`, indicating running in a Node.js compatible runtime.
 */
const isNode = () => globalThis.process?.release?.name === 'node'

/**
 * Indicates if running in Bun runtime.
 */
const isBun = () => !!globalThis.Bun || !!globalThis.process?.versions?.bun

/**
 * Indicates if running in Deno runtime.
 */
const isDeno = () => !!globalThis.Deno

/**
 * Indicates if running in Netlify runtime.
 */
const isNetlify = () => typeof globalThis.Netlify === 'object'

/**
 * Indicates if running in EdgeLight (Vercel Edge) runtime.
 */
const isEdgeLight = () => typeof globalThis.EdgeRuntime === 'object'

/**
 * Indicates if running in Cloudflare Workers runtime.
 * See: https://developers.cloudflare.com/workers/runtime-apis/web-standards/#navigatoruseragent
 */
const isWorkerd = () => globalThis.navigator?.userAgent === 'Cloudflare-Workers'

function detectRuntime(): RuntimeName {
  // Note: we're currently not taking 'fastly' into account. Why?
  const runtimeChecks = [
    [isNetlify, 'netlify'],
    [isEdgeLight, 'edge-light'],
    [isWorkerd, 'workerd'],
    [isDeno, 'deno'],
    [isBun, 'bun'],
    [isNode, 'node'],
  ] as const

  const detectedRuntime =
    runtimeChecks
      // TODO: Transforming destructuring to the configured target environment ('chrome58', 'edge16', 'firefox57', 'safari11') is not supported yet,
      // so we can't write the following code yet:
      // ```
      // .flatMap(([isCurrentRuntime, runtime]) => isCurrentRuntime() ? [runtime] : [])
      // ```
      .flatMap((check) => (check[0]() ? [check[1]] : []))
      .at(0) ?? ''

  return detectedRuntime
}

const runtimesPrettyNames = {
  node: 'Node.js',
  workerd: 'Cloudflare Workers',
  deno: 'Deno and Deno Deploy',
  netlify: 'Netlify Edge Functions',
  'edge-light':
    'Edge Runtime (Vercel Edge Functions, Vercel Edge Middleware, Next.js (Pages Router) Edge API Routes, Next.js (App Router) Edge Route Handlers or Next.js Middleware)',
} as const

type GetRuntimeOutput = {
  id: RuntimeName
  prettyName: string
  isEdge: boolean
}

export function getRuntime(): GetRuntimeOutput {
  const runtimeId = detectRuntime()

  return {
    id: runtimeId,
    // Fallback to the runtimeId if the runtime is not in the list
    prettyName: runtimesPrettyNames[runtimeId] || runtimeId,
    isEdge: ['workerd', 'deno', 'netlify', 'edge-light'].includes(runtimeId),
  }
}

export const HOUSE_EDGE = 0.06;

// Row index system:
// - Canonical storage/transport is 0-based (0..MAX_ROW_INDEX)
// - UI may display rowIndex + 1 when showing "Row N"
export const MIN_ROW_INDEX = 0;
export const MAX_ROW_INDEX = 13;
export const CENTER_ROW_INDEX = (MIN_ROW_INDEX + MAX_ROW_INDEX) / 2; // 6.5

// 1% price change => 10 rows
export const PRICE_SENSITIVITY = 1000;

export const MIN_MULTIPLIER = 1.01;
export const MAX_MULTIPLIER = 100;

export function clampRowIndex(rowIndex: number): number {
  return Math.max(MIN_ROW_INDEX, Math.min(MAX_ROW_INDEX, rowIndex));
}

// Money is stored as Decimal(18,2) in DB, so always round payouts/balances to cents.
export function roundMoney(amount: number): number {
  return Math.round((amount + Number.EPSILON) * 100) / 100;
}

export function calculateMultiplier(
  targetRow: number,
  referenceRow: number = CENTER_ROW_INDEX,
  timeDeltaSeconds: number = 0
): number {
  const distance = Math.abs(targetRow - referenceRow);

  const sigma = 3.5;
  const baseProbability = 0.92;

  // Apply time bonus to probability calculation to maintain house edge
  const timeBonus = 1 + Math.max(0, timeDeltaSeconds) * 0.04;
  const adjustedProbability = (baseProbability * Math.exp(-(distance * distance) / (2 * sigma * sigma))) / timeBonus;

  // Avoid Infinity in pathological cases.
  const fairPayout = adjustedProbability > 0 ? 1 / adjustedProbability : MAX_MULTIPLIER;
  const housePayout = fairPayout * (1 - HOUSE_EDGE);

  const clamped = Math.max(MIN_MULTIPLIER, Math.min(MAX_MULTIPLIER, housePayout));
  return Math.round(clamped * 10000) / 10000;
}
